import "./App.css";
import React from "react";

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      strg1: "",
      strg2: "",
      cstrg: "",
      data : [] 
    };

    this.function1 = () =>{
      console.log("Helo")
    }

    this.myValue = "123"

  }

  // wordCounter(props) {
  //   this.w1 = this.str1;
  //   console.log(this.w1);
  //   this.w2 = this.str2;
  // }

  concat=()=> {



    if(this.state.strg1 && this.state.strg2){
      this.setState({
        cstrg: this.state.strg1.concat(this.state.strg2),
      });
    }


  }

  setStrg1 = (event) => {

    console.log("myValue: ", this.myValue)

    this.setState({
      strg1: event.target.values
    })

  }


  render() {
    return (
      <div className="App">
        <p>Word Concat</p>

        <br />


        <input
          type="text"
          onChange={(event) => {
            this.setStrg1(event)
          }}
          value={this.state.strg1}
        />


        <br />
        {this.state.strg1}

        <br />

        <input
          type="text"
          onChange={(event) => {
           this.setState({
            strg2: event.target.value
           })
          }}
          value={this.state.strg2}
        />



        <br />
        {this.state.strg2}
        <br />

        <button
          onClick={() => {
            this.concat();
          }}
        >
          Enter
        </button>


        <br />
        <br />
        {this.state.cstrg}
      </div>
    );
  }
}
export default App;
