import "./App.css";
import React from "react";

class App extends React.Component {
  str=''
  constructor(props) {
    super(props);
    this.state = {
      str: "_____",
      str1:'',
      str2:'',
    };
  }
  wordCounter() {
    this.setState({
      str: str1 + str2,
    });
  }

  render() {
    return (
      <div className="App">
        <p>Word Concat</p>
        <input type="text" value={this.str1}/>
        <br />
        <br />
        <input type="text" value={this.str2}/>
        <br />
        <br />
        {this.state.str}
        <button onClick={this.wordCounter}></button>
        {str}
      </div>
    );
  }
}
export default App;
