import "./App.css";
import React from "react";

class App extends React.Component {
  str = "";
  constructor(props) {
    super(props);
    this.state = {
      count: 0,
      str: '',
    };
  }

  counter() {
    this.setState({
      count: this.state.count + 1,
    });
  }

  wordInput(){
    this.setState({
      str: this.str.concat(this.str)
    })
  }

  render() {
    return (
      <div className="App">

        <input onChange={ ()=> {this.wordInput()}} type="text" ></input>
        <br />
        {this.state.count}
        <br />
        <button
          onClick={() => {
            this.counter();
          }}
        >
          Count
        </button>
        <br />
      </div>
    );
  }
}
export default App;
