import "./App.css";
import React from "react";

class App extends React.Component {

  render() {
    return (
      <div className="App">
        <p>Word Concat</p>
        <input type="text" value={this.str1}/>
        <br />
        <br />
        <input type="text" value={this.str2}/>
        <br />
        <br />
        {this.state.str}
        <button onClick={this.wordCounter}></button>
      </div>
    );
  }
}
export default App;
