import "./App.css";
import React from "react";

class App extends React.Component {
  str=''
  constructor(props) {
    super(props);
    this.state = {
      str: "Hello",
    };
  }

  render() {
    return (
      <div className="App">
        <p>Word Concat</p>
        <input type="text" onChange={this.wordCounter} value={this.str1}/>
        <br />
        <br />
        <input type="text" onChange={this.wordCounter} value={this.str2}/>
        <br />
        <br />

        <button onClick={this.concat}>Enter</button><br /><br />
        {this.w1+this.w2}
        {this.state.str}
      </div>
    );
  }
}
export default App;
