    super(props);
    this.state = {
      str: "_____",
    };
  }
  wordCounter(props) {
    this.setState(this.str1);
    this.setState(this.str2);
    this.str = this.str1+this.str2
    };

  render() {
    return (
      <div className="App">
        enable
        <p>Word Concat</p>
        <input type="text" onChange={this.wordCounter} value={this.str1}/>
        <br />
        <br />
        <input type="text" onChange={this.wordCounter} value={this.str2}/>
        <br />
        <br />
        {this.state.str}
        <br /><br />
        <button onClick={this.wordCounter}>Enter</button>
      </div>
    );
  }
}
export default App;
