import "./App.css";
import React from "react";

class App extends React.Component {
  str=''
  constructor(props) {
    super(props);
    this.state = {
      str: "",
    };
  }

  concat() {
    this.setState({
      w1: this.w1,
      w2: this.w2,
      str: this.w1.concat(this.w2),
    })
  }
  addNewText = (text) => {
    this.str.concat(text);

  }
  render() {
    return (
      <div className="App">
        <p>Word Concat</p>
        <form className ="App" onSubmit={this.concat()}>

          <input type = 'text' value={ () => {this.addNewText()}}/>
          <br />
          <input type = 'text' value={ () => {this.addNewText()}}/>
          <br />
          <button type = 'submit'> Enter </button>
        </form>
        {this.state.str}

      </div>
    );
  }
}
export default App;
