import "./App.css";
import React from "react";

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      strg1: "",
      strg2: "",
      cstrg: "",
    };
  }

  // wordCounter(props) {
  //   this.w1 = this.str1;
  //   console.log(this.w1);
  //   this.w2 = this.str2;
  // }

  concat() {
    console.log("w1: ", this.w1);
    this.setState({
      str: this.w1.concat(this.w2),
    });
  }

  setStrg1 = (event) => {

    this.setState({
      strg1: event.target.value
    })

  }


  render() {
    return (
      <div className="App">
        <p>Word Concat</p>

        <br />


        <input
          type="text"
          onChange={(event) => {
            this.setStrg1(event)
          }}
          value={this.strg1}
        />


        <br />
        {this.state.strg1}

        <br />

        <input
          type="text"
          onChange={(event) => {
           this.setState({
            strg2: event.target.value
           })
          }}
          value={this.state.strg2}
        />

        <input
          type="text"
          value={this.state.strg2}
        />

        <br />
        {this.state.strg2}
        <br />

        <button
          onClick={() => {
            this.concat();
          }}
        >
          Enter
        </button>
        <br />
        <br />
        {this.state.str}
      </div>
    );
  }
}
export default App;
