import "./App.css";
import React from "react";

class App extends React.Component {
  str=''
  constructor(props) {
    super(props);
    this.state = {
      str: "___",
    };
  }

  concat() {
    this.setState({
      str: this.w1.concat(this.w2),
    })
  }
  render() {
    return (
      <div className="App">
        <p>Word Concat</p>

        <br />
        <input type="text" onChange={ () => {this.concat()}} value={this.str1}/>
        <br />
        {this.w1}
        <br />
        <input type="text" onChange={() => {this.concat()}} value={this.str2}/>
        <br />
        {this.w2}
        <br />

        <button onClick={() => {this.concat()}}>Enter</button><br /><br />
        {this.state.str}
      </div>
    );
  }
}
export default App;
